mapper.select:
select id, phone 'phone', name, is_introduce 'isIndroduce', introducer, activate_date 'activateDate', birthday, gender 'gender', no_phone 'noPhone', addr 'address', shop_id 'shopId', is_pass 'isPass', is_abandon 'isAbandon', pass_shop_id 'passShopId', is_active 'isActive', is_introduce 'isIntroduce', is_sleep 'isSleep', pass_time 'passTime', standard_time 'standardTime', status 'status', store_balance 'storeBalance', point_balance 'pointBalance', treatment_balance 'treatmentBalance', stock_balance 'stockBalance', owe_balance 'oweBalance', create_date 'createDate' from shop_s_member_card
and id = #{id}
and phone = #{phone}
and status = #{status}
and is_pass = #{isPass}

SELECT a.`id` 'id', a.`no` 'no', a.is_express 'isExpress', a.is_sale 'isSale', a.`name` 'name', a.`bulk_unit` 'bulkUnit', a.`boxes_unit` 'boxesUnit', b.`market_price` 'marketPrice', b.`boxes_market_price` 'boxesMarketPrice', b.promotion_price 'promotionPrice', b.boxes_promotion_price 'boxesPromotionPrice', b.experience_price 'experiencePrice', b.boxes_experience_price 'boxesExperiencePrice', a.`type` 'type', a.`status` 'status', a.`update_status` 'updateStatus', a.`show_status` 'showStatus', a.`create_date_time` 'createDateTime', a.num, a.num 'unitChangeNumber', a.standard FROM shop_b_product_info a LEFT JOIN shop_b_product_price b ON b.product_id = a.id
a.id = #{id}
a.no = #{no}
a.status = #{status}

SELECT `id`, `no`, `name`, `status`, is_sale 'isSale', is_calculate_sal 'isCalculateSal', is_come 'isCome', is_consume 'isConsume', `type`, DATE_FORMAT(`create_date_time`,'%Y-%m-%d %H:%i:%S') 'createDateTime', effective_months 'effectiveMonths' FROM shop_b_project_info
AND id = #{id}
AND no = #{no}
AND status = #{status}

SELECT id, no, name, unit, price, percent, effective_cycle 'effectiveCycle', no_limit_price 'noLimitPrice', is_wenxiu 'isWenxiu', is_calculate_sal 'isCalculateSal', is_come 'isCome', is_consume 'isConsume', first_earn 'firstEarn', is_count_comsume 'isCountConsume', status FROM shop_b_cooperation_project
AND id = #{id}
AND no = #{no}
AND status = #{status}

SELECT id, member_card_id memberCardId, shop_id shopId, consumption_date consumptionDate, create_user_id createUserId, create_date createDate, status, is_link_fail isLinkFail, fail_id failId, fail_date failDate, is_over_fail isOverFail, is_cooperation isCooperation, region, maker_id makerId, ostype, fail_user_id failUserId, is_tuoke isTuoke, fail_earn failEarn, is_detail_pay isDetailPay, receipt_shop_type receiptShopType FROM shop_s_consumption
AND id = #{id}
AND member_card_id = #{memberCardId}
AND shop_id = #{shopId}
