SELECT id, consumption_id consumptionId, member_card_id memberCardId, payment, bank_card_pay bankcardPay, cash_pay cashPay, create_date createDate, owe, status, store_pay storePay, is_book isBook, effective_earn effectiveEarn, real_payment realPayment, remark, is_fail isFail, shopid shopId, buy_owe buyOwe, buy_type buyType, activity_id activityId, adviser, cosmetologist, cosmetologist2, cosmetologist3 FROM shop_s_consumption_cooperation_project
AND id = #{id}
AND consumption_id = #{consumptionId}
AND member_card_id = #{memberCardId}
AND is_fail = #{isFail}

