mapper.select:
select b.shop_id 'shopId', sum(a.number) 'consumeNumber', sum(case c.buy_unit when e.bulk_unit then (a.number * c.price) when e.boxes_unit then (c.price / e.num * a.number) else 0 end) 'consumeMoney', 'product' as 'type', null as 'data', null as 'data2' from shop_s_consumption_product_get a inner join shop_s_consumption b on a.consumption_id = b.id inner join shop_s_consumption_product_detail c on a.product_detail_id = c.id inner join shop_s_member_card d on d.id = b.member_card_id inner join shop_b_product_info e on e.id = c.product_id
and b.`status` = #{status}
and d.is_pass = #{isPass}
and d.pass_time <= b.consumption_date
and b.shop_id in
#{item}
and b.consumption_date >= #{beginConsumeDate}
and b.consumption_date <= #{endConsumeDate}
group by b.shop_id
union all
SELECT n.shop_id 'shopId', SUM(a.number) 'consumeNumber', SUM(CASE c.buy_unit WHEN e.bulk_unit THEN (a.number * c.price) WHEN e.boxes_unit THEN (c.price / e.num * a.number) ELSE 0 END) 'consumeMoney', 'productNegative' AS 'type', NULL AS 'data', NULL AS 'data2' FROM `shop_s_negative_number` n INNER JOIN shop_s_consumption_product_get a ON n.detail_id = a.product_detail_id AND n.id=a.`order_id` INNER JOIN shop_s_consumption_product_detail c ON a.product_detail_id = c.id INNER JOIN shop_s_member_card d ON d.id = n.member_card_id INNER JOIN shop_b_product_info e ON e.id = c.product_id
and d.is_pass = #{isPass}
and n.shop_id in
#{item}
and n.create_date_time >= #{beginConsumeDate}
and n.create_date_time <= #{endConsumeDate}
group by n.shop_id
union all
select b.shop_id 'shopId', sum(a.done_number) 'consumeNumber', sum(a.done_number * c.price) 'consumeMoney', 'project' as 'type', null as 'data', null as 'data2' from shop_s_consumption_project_done a inner join shop_s_consumption b on a.consumption_id = b.id inner join shop_s_consumption_project_detail c on a.project_detail_id = c.id inner join shop_s_member_card d on d.id = b.member_card_id
and b.`status` = #{status}
and d.is_pass = #{isPass}
and d.pass_time <= b.consumption_date
and c.activity_id not in
and b.shop_id in
#{item}
and b.consumption_date >= #{beginConsumeDate}
and b.consumption_date <= #{endConsumeDate}
group by b.shop_id
union all
SELECT n.shop_id 'shopId', SUM(a.done_number) 'consumeNumber', SUM(a.done_number * c.price) 'consumeMoney', 'projectNegative' AS 'type', NULL AS 'data', NULL AS 'data2' FROM `shop_s_negative_number` n INNER JOIN shop_s_consumption_project_done a ON a.project_detail_id = n.detail_id AND n.id=a.`order_id` INNER JOIN shop_s_consumption_project_detail c ON a.project_detail_id = c.id INNER JOIN shop_s_member_card d ON d.id = n.member_card_id
and d.is_pass = #{isPass}
and c.activity_id not in
and n.shop_id in
#{item}
and n.create_date_time >= #{beginConsumeDate}
and n.create_date_time <= #{endConsumeDate}
group by n.shop_id
union all
SELECT n.shop_id 'shopId', SUM(a.done_number) 'consumeNumber', SUM(a.done_number * c.price) 'consumeMoney', 'projectWxNegative' AS 'type', NULL AS 'data', NULL AS 'data2' FROM `shop_s_negative_number` n INNER JOIN shop_s_wx_consumption_project_done a ON a.project_detail_id = n.detail_id AND n.id=a.`consumption_id` INNER JOIN shop_s_wx_consumption_project_detail c ON a.project_detail_id = c.id INNER JOIN shop_s_member_card d ON d.id = n.member_card_id
and d.is_pass = #{isPass}
and n.shop_id in
#{item}
and n.create_date_time >= #{beginConsumeDate}
and n.create_date_time <= #{endConsumeDate}
group by n.shop_id
union all
select a.service_shop_id 'shopId', sum(a.service_number) 'consumeNumber', sum(a.service_number * c.price * a.percent) 'consumeMoney', 'cooperation' as 'type', e.is_wenxiu as 'data', a.is_count_comsume as 'data2' from shop_s_consumption_cooperation_project_done a inner join shop_s_consumption b on a.consumption_id = b.id inner join shop_s_consumption_cooperation_project_detail c on a.detail_id = c.id inner join shop_s_member_card d on d.id = b.member_card_id inner join shop_b_cooperation_project e on e.id = c.cooperation_id
and a.is_fail = 0
and a.is_fail = 1
and d.is_pass = #{isPass}
and d.pass_time <= b.consumption_date
and a.service_shop_id in
#{item}
and a.service_date >= #{beginConsumeDate}
and a.service_date <= #{endConsumeDate}
and (case e.is_wenxiu when 1 then 1=1 else b.consumption_date >= #{beginSellDate} end)
group by a.service_shop_id,e.is_wenxiu,a.is_count_comsume
