mapper.select:
SELECT id, no, name, parent_id parentId, status, create_date_time createDateTime, create_user_id createUserId, is_shop isShop, region, chief_id chiefId, address, is_schedule_alert isScheduleAlert, region_id regionId FROM oa_department_structure
and id = #{id}
and name = #{name}
and no = #{no}
and name LIKE CONCAT(CONCAT('%', #{nameLike}), '%')
and name NOT LIKE CONCAT(CONCAT('%', #{nameNotLike}), '%')
and status = #{status}
and is_shop = #{isShop}
ORDER BY create_date_time ASC

select id, phone 'phone', name, is_introduce 'isIndroduce', introducer, activate_date 'activateDate', birthday, gender 'gender', no_phone 'noPhone', addr 'address', shop_id 'shopId', is_pass 'isPass', is_abandon 'isAbandon', pass_shop_id 'passShopId', pass_consumption_id 'passConsumptionId', is_active 'isActive', is_introduce 'isIntroduce', is_sleep 'isSleep', pass_time 'passTime', standard_time 'standardTime', status 'status', store_balance 'storeBalance', point_balance 'pointBalance', treatment_balance 'treatmentBalance', wenxiu_balance 'wenxiuBalance', stock_balance 'stockBalance', owe_balance 'oweBalance', is_new_pass 'isNewPass', new_pass_time 'newPassTime', new_pass_shop_id 'newPassShopId', new_pass_consumption_id 'newPassConsumptionId', create_date 'createDate' from shop_s_member_card
and id = #{id}
and phone = #{phone}
and name = #{name}
and shop_id = #{shopId}
and status = #{status}
and is_pass = #{isPass}
and pass_shop_id = #{passShopId}

mapper.update:
UPDATE shop_s_member_card
phone = #{phone}, name = #{name}, activate_date = #{activateDate}, birthday = #{birthday}, shop_id = #{shopId}, gender = #{gender}, ages = #{ages}, from_type = #{fromType}, job = #{job}, addr = #{addr}, remark = #{remark}, no_phone = #{noPhone}, create_user_id = #{createUserId}, create_date = #{createDate}, status = #{status}, is_pass = #{isPass}, pass_time = #{passTime}, is_active = #{isActive}, is_abandon = #{isAbandon}, is_sleep = #{isSleep}, store_balance = #{storeBalance}, point_balance = #{pointBalance}, treatment_balance = #{treatmentBalance}, stock_balance = #{stockBalance}, wenxiu_balance = #{wenxiuBalance}, owe_balance = #{oweBalance}, is_introduce = #{isIntroduce}, introducer = #{introducer}, is_standard = #{isStandard}, standard_time = #{standardTime}, pass_shop_id = #{passShopId}, pass_consumption_id = #{passConsumptionId}, left_total = #{leftTotal}, channel_id = #{channelId}, last_update_time = #{lastUpdateTime}, error = #{error}, label = #{label}, period = #{period}, free_start = #{freeStart}, free_end = #{freeEnd}, remind_type = #{remindType}, is_new_pass = #{isNewPass}, is_new_sleep = #{isNewSleep}, is_new_abandon = #{isNewAbandon}, new_pass_consumption_id = #{newPassConsumptionId}, new_pass_shop_id = #{newPassShopId}, new_pass_time = #{newPassTime}, is_new_tuoke = #{isNewTuoke}, new_tuoke_consumption_id = #{newTuokeConsumptionId}, new_tuoke_shop_id = #{newTuokeShopId}, new_tuoke_time = #{newTuokeTime}, open_id = #{openId}, is_sign = #{isSign}
WHERE id = #{id}

mapper.select:
SELECT id, no, name, bulk_unit bulkUnit, boxes_unit boxesUnit, market_price marketPrice, boxes_market_price boxesMarketPrice, type, status, create_date_time createDateTime, num, promotion_price promotionPrice, boxes_promotion_price boxesPromotionPrice, experience_price experiencePrice, boxes_experience_price boxesExperiencePrice, effective_months effectiveMonths, standard, update_status updateStatus, show_status showStatus, is_express isExpress, is_sale isSale FROM shop_b_product_info
a.id = #{id}
a.no = #{no}
and name = #{name}
a.status = #{status}

SELECT id, no, name, type, status, create_date_time createDateTime, effective_months effectiveMonths, update_status updateStatus, show_status showStatus, is_sale isSale, is_calculate_sal isCalculateSal, is_come isCome, is_consume isConsume FROM shop_b_project_info
AND id = #{id}
AND no = #{no}
and name = #{name}
AND status = #{status}

SELECT id, no, name, unit, price, percent, effective_cycle effectiveCycle, no_limit_price noLimitPrice, create_user_id createUserId, create_date_time createDateTime, status, is_wenxiu isWenxiu, first_earn firstEarn, is_count_comsume isCountComsume, is_calculate_sal isCalculateSal, is_come isCome, is_consume isConsume FROM shop_b_cooperation_project
AND id = #{id}
AND no = #{no}
and name = #{name}
AND status = #{status}

SELECT id, member_card_id memberCardId, shop_id shopId, consumption_date consumptionDate, create_user_id createUserId, create_date createDate, status, is_link_fail isLinkFail, fail_id failId, fail_date failDate, is_over_fail isOverFail, is_cooperation isCooperation, region, maker_id makerId, ostype, fail_user_id failUserId, is_tuoke isTuoke, fail_earn failEarn, is_detail_pay isDetailPay, receipt_shop_type receiptShopType FROM shop_s_consumption
AND id = #{id}
AND member_card_id = #{memberCardId}
AND shop_id = #{shopId}
