mapper.select:
SELECT id, no, name, parent_id parentId, status, create_date_time createDateTime, create_user_id createUserId, is_shop isShop, region, chief_id chiefId, address, is_schedule_alert isScheduleAlert, region_id regionId FROM oa_department_structure
and id = #{id}
and name = #{name}
and no = #{no}
and name LIKE CONCAT(CONCAT('%', #{nameLike}), '%')
and name NOT LIKE CONCAT(CONCAT('%', #{nameNotLike}), '%')
and status = #{status}
and is_shop = #{isShop}

select id, phone 'phone', name, is_introduce 'isIndroduce', introducer, activate_date 'activateDate', birthday, gender 'gender', no_phone 'noPhone', addr 'address', shop_id 'shopId', is_pass 'isPass', is_abandon 'isAbandon', pass_shop_id 'passShopId', is_active 'isActive', is_introduce 'isIntroduce', is_sleep 'isSleep', pass_time 'passTime', standard_time 'standardTime', status 'status', store_balance 'storeBalance', point_balance 'pointBalance', treatment_balance 'treatmentBalance', stock_balance 'stockBalance', owe_balance 'oweBalance', create_date 'createDate' from shop_s_member_card
and id = #{id}
and phone = #{phone}
and status = #{status}
and is_pass = #{isPass}

SELECT id, no, name, bulk_unit bulkUnit, boxes_unit boxesUnit, market_price marketPrice, boxes_market_price boxesMarketPrice, type, status, create_date_time createDateTime, num, promotion_price promotionPrice, boxes_promotion_price boxesPromotionPrice, experience_price experiencePrice, boxes_experience_price boxesExperiencePrice, effective_months effectiveMonths, standard, update_status updateStatus, show_status showStatus, is_express isExpress, is_sale isSale FROM shop_b_product_info
a.id = #{id}
a.no = #{no}
a.status = #{status}

SELECT id, no, name, type, status, create_date_time createDateTime, effective_months effectiveMonths, update_status updateStatus, show_status showStatus, is_sale isSale, is_calculate_sal isCalculateSal, is_come isCome, is_consume isConsume FROM shop_b_project_info
AND id = #{id}
AND no = #{no}
AND status = #{status}

SELECT id, no, name, unit, price, percent, effective_cycle effectiveCycle, no_limit_price noLimitPrice, create_user_id createUserId, create_date_time createDateTime, status, is_wenxiu isWenxiu, first_earn firstEarn, is_count_comsume isCountComsume, is_calculate_sal isCalculateSal, is_come isCome, is_consume isConsume FROM shop_b_cooperation_project
AND id = #{id}
AND no = #{no}
AND status = #{status}

SELECT id, member_card_id memberCardId, shop_id shopId, consumption_date consumptionDate, create_user_id createUserId, create_date createDate, status, is_link_fail isLinkFail, fail_id failId, fail_date failDate, is_over_fail isOverFail, is_cooperation isCooperation, region, maker_id makerId, ostype, fail_user_id failUserId, is_tuoke isTuoke, fail_earn failEarn, is_detail_pay isDetailPay, receipt_shop_type receiptShopType FROM shop_s_consumption
AND id = #{id}
AND member_card_id = #{memberCardId}
AND shop_id = #{shopId}
