mapper.select:
SELECT id, consumption_id consumptionId, member_card_id memberCardId, payment, bank_card_pay bankcardPay, cash_pay cashPay, create_date createDate, owe, status, store_pay storePay, is_book isBook, effective_earn effectiveEarn, real_payment realPayment, remark, is_fail isFail, shopid shopId, buy_owe buyOwe, buy_type buyType, activity_id activityId, adviser, cosmetologist, cosmetologist2, cosmetologist3 FROM shop_s_consumption_cooperation_project
AND id = #{id}
AND consumption_id = #{consumptionId}
AND member_card_id = #{memberCardId}
AND is_fail = #{isFail}

SELECT id, consumption_id consumptionId, consumption_cooperation_id consumptionCooperationId, activity_id activityId, cooperation_id cooperationId, consumption_set_id consumptionSetId, member_card_id memberCardId, buy_number buyNumber, unit, sort, left_number leftNumber, status, is_fail isFail, shopid shopId, coupon, consumption_date consumptionDate, price, left_earn leftEarn, left_store_earn leftStoreEarn, channel_id channelId, buy_type buyType FROM shop_s_consumption_cooperation_project_detail
AND id = #{id}
AND consumption_id = #{consumptionId}
AND consumption_cooperation_id = #{consumptionCooperationId}
AND consumption_set_id = #{consumptionSetId}
AND member_card_id = #{memberCardId}

SELECT id, consumption_id consumptionId, detail_id detailId, service_date serviceDate, service_number serviceNumber, remark, is_fail isFail, create_date createDate, service_shop_id serviceShopId, share_left_earn shareLeftEarn, price, service_company serviceCompany, percent, first_earn firstEarn, is_count_comsume isCountComsume, doctor, nurse remark FROM shop_s_consumption_cooperation_project_done
AND id = #{id}
AND consumption_id = #{consumptionId}
AND detail_id = #{detailId}
AND service_shop_id = #{serviceShopId}
AND is_fail = #{isFail}
