mapper.select:
SELECT id, consumption_id 'consumptionId', store_pay 'storePay', bankcard_pay 'bankcardPay', cash_pay cashPay, point_pay pointPay, is_book isBook, payment, effective_earn effectiveEarn, remark, real_payment realPayment, owe, member_card_id memberCardId, create_date createDate, is_fail isFail, shopid 'shopId', buy_owe buyOwe, is_detail_pay isDetailPay FROM shop_s_consumption_product
AND id = #{id}
AND consumption_id = #{consumptionId}
AND member_card_id = #{memberCardId}
AND is_fail = #{isFail}

SELECT id, consumption_id 'consumptionId', consumption_product_id 'consumptionProductId', product_id 'productId', activity_id 'activityId', buy_number buyNumber, left_number leftNumber, coupon, is_introduce isIntroduce, introducer, member_card_id memberCardId, create_date createDate, is_send isSend, buy_unit buyUnit, consumption_set_id consumptionSetId, price, shop_id shopId, is_fail isFail, experience_price experiencePrice, promotion_price promotionPrice, market_price marketPrice, left_unit leftUnit, buy_type buyType, express_type expressType, channel_id channelId, payment, store_pay storePay, bankcard_pay bankcardPay, cash_pay cashPay, is_book isBook, owe, buy_owe buyOwe, effective_earn effectiveEarn, real_payment realPayment, is_pay, remark, actual_number actualNumber, actual_price actualPrice, actual_unit actualUnit FROM shop_s_consumption_product_detail
AND id = #{id}
AND consumption_id = #{consumptionId}
AND consumption_product_id = #{consumptionProductId}
AND consumption_set_id = #{consumptionSetId}
AND member_card_id = #{memberCardId}

mapper.update:
UPDATE shop_s_consumption_product_detail
consumption_id = #{consumptionId}, consumption_product_id = #{consumptionProductId}, product_id = #{productId}, activity_id = #{activityId}, buy_number = #{buyNumber}, left_number = #{leftNumber}, coupon = #{coupon}, is_introduce = #{isIntroduce}, introducer = #{introducer}, member_card_id = #{memberCardId}, create_date = #{createDate}, is_send = #{isSend}, buy_unit = #{buyUnit}, consumption_set_id = #{consumptionSetId}, price = #{price}, shop_id = #{shopId}, is_fail = #{isFail}, experience_price = #{experiencePrice}, promotion_price = #{promotionPrice}, market_price = #{marketPrice}, left_unit = #{leftUnit}, buy_type = #{buyType}, express_type = #{expressType}, channel_id = #{channelId}, payment = #{payment}, store_pay = #{storePay}, bankcard_pay = #{bankCardPay}, cash_pay = #{cashPay}, is_book = #{isBook}, owe = #{owe}, buy_owe = #{buyOwe}, effective_earn = #{effectiveEarn}, real_payment = #{realPayment}, is_pay = #{isPay}, remark = #{remark}, actual_number = #{actualNumber}, actual_price = #{actualPrice}, actual_unit = #{actualUnit}
WHERE id = #{id}

