SELECT id, consumption_id 'consumptionId', store_pay 'storePay', bankcard_pay 'bankcardPay', cash_pay cashPay, point_pay pointPay, is_book isBook, payment, effective_earn effectiveEarn, remark, real_payment realPayment, owe, member_card_id memberCardId, create_date createDate, is_fail isFail, shopid 'shopId', buy_owe buyOwe, is_detail_pay isDetailPay FROM shop_s_consumption_product
AND id = #{id}
AND consumption_id = #{consumptionId}
AND member_card_id = #{memberCardId}
AND is_fail = #{isFail}

