mapper.select:
select g.id 'orderId', b.id 'businessId', b.fail_earn 'failEarn', a.project_id 'projectId', a.real_payment 'projectPrice', a.activity_id 'activityId', d.`name` 'activityName', a.buy_number 'buyNumber', a.coupon 'coupon', a.is_send 'isSend', a.member_card_id 'cardId', a.price 'buyPrice', a.is_count 'isCount', b.shop_id 'shopId', b.consumption_date 'consumeDate', b.`status` 'status', b.fail_date 'failDate', b.region 'area', ifnull(a.payment,0) 'shouldPay', ifnull(a.real_payment,0) 'realPay', ifnull(a.store_pay,0) 'store', ifnull(a.bankcard_pay,0) 'pos', ifnull(a.cash_pay,0) 'cash', ifnull(a.point_pay,0) 'point', a.is_book 'isBook', g.remark, g.is_birthday 'isBirthday', a.buy_owe 'owe', a.is_cash_coupon 'isCashCoupon' from shop_s_consumption_project_detail a inner join shop_s_consumption b on a.consumption_id = b.id and b.is_cooperation = 0 and a.consumption_set_id is null left join shop_s_consumption_project g on g.consumption_id = a.consumption_id
and a.project_id = #{projectId}
and b.shop_id in
#{item}
and b.`status` = #{status}
and b.consumption_date >= #{beginConsumeDate}
and b.consumption_date <= #{endConsumeDate}
limit #{start},#{count}

SELECT id, consumption_id 'consumptionId', store_pay 'storePay', bankcard_pay 'bankcardPay', cash_pay cashPay, point_pay pointPay, is_book isBook, payment, effective_earn effectiveEarn, remark, is_birthday isBirthday, real_payment realPayment, owe, member_card_id memberCardId, is_cash_coupon isCashCoupon, create_date createDate, is_fail isFail, shopid 'shopId', cash_coupon cashCoupon, buy_owe buyOwe, is_detail_pay isDetailPay FROM shop_s_consumption_project
AND id = #{id}
AND consumption_id = #{consumptionId}
AND member_card_id = #{memberCardId}
AND is_fail = #{isFail}

SELECT id, consumption_id consumptionId, consumption_project_id consumptionProjectId, project_id projectId, activity_id activityId, buy_number buyNumber, number, coupon, is_send isSend, create_date createDate, end_date endDate, member_card_id memberCardId, consumption_set_id consumptionSetId, price, service_time serviceTime, is_count isCount, shop_id shopId, is_fail isFail, experience_price experiencePrice, promotion_price promotionPrice, market_price marketPrice, buy_type buyType, channel_id channelId, payment, store_pay storePay, bankcard_pay bankcardPay, cash_pay cashPay, point_pay pointPay, is_book isBook, owe, buy_owe buyOwe, effective_earn effectiveEarn, real_payment realPayment, is_pay isPay, remark, is_cash_coupon isCashCoupon, is_tuoke isTuoke, service_type serviceType FROM shop_s_consumption_project_detail
AND id = #{id}
AND consumption_id = #{consumptionId}
AND consumption_project_id = #{consumptionProjectId}
AND consumption_set_id = #{consumptionSetId}
AND member_card_id = #{memberCardId}

